'use strict';

/**
 * Struct to maintain data tree relationship while iterating XML doc.
 * @param {String} name   the current node's name
 * @param {Node}   parent this node's parent node or null
 * @param {Object} data   any arbitrary data: object, string, etc.
 */
var Node = module.exports = function Node(name, type, parent, data) {
	this._name = name;
	this._type = type;
	this._parent = parent;
	this._data = data;

	if (parent && name) {
		parent.data[name] = data;
	}
};

Node.prototype = {
	get name() {
		return this._name;
	},
	get type() {
		return this._type;
	},
	get parent() {
		return this._parent;
	},
	get data() {
		return this._data;
	},
	set data(value) {
		this._data = value;
		if (this._parent) {
			this._parent.data[this._name] = value;
		}
	}
};