var path = require('path'),
	dust = require('dustjs-linkedin');

function extendDust(extensions) {
	extensions.forEach(function (extension) {
		require(path.resolve(extension))(dust);
	});
}

module.exports = {
	config     : require('./lib/config'),
	dust       : dust,
	extendDust : extendDust,
	expressDust: require('./lib/mvc/express-dust'),
	rescompile : require('./lib/middleware/rescompile'),
	routes     : require('./lib/mvc/routes')
};
