var path = require('path'),
	cliUtil = require('../util');

var TEMPLATE_TEMPLATE = [
	'{! Template: \'{template}\' !}',
	'Hello, {template}!'
].join(cliUtil.eol);

module.exports = {
	generate: function(name) {
		var destination = path.join(process.cwd(), 'public', 'templates');

		cliUtil.mkdir(destination, function(dir) {
			var dest = path.join(dir, name + '.dust');
			cliUtil.write(dest, TEMPLATE_TEMPLATE.replace(/\{template\}/g, name));
		});
	}
};
