#!/usr/bin/env node

var crypto = require('crypto'),
    read = require('read');






read({ prompt: 'Enter your password: ', silent: true }, function (err, data) {
    var shasum = crypto.createHash('sha1');
    shasum.update(data, 'ascii');
    read({ prompt: 'Enter salt:'}, function (err, data) {
        shasum.update(data, 'ascii');
        console.log(shasum.digest('hex'));
    });
});

